import React from 'react';
import Map from '../components/Map';  // Corrigir a importação

const RideOptions: React.FC = () => {
  // Aqui, você receberá as opções da API e a rota
  const route = {
    start: [51.505, -0.09],  // Exemplo de coordenadas de origem
    end: [51.515, -0.1],     // Exemplo de coordenadas de destino
    path: [
      [51.505, -0.09],
      [51.515, -0.1]
    ]
  };

  return (
    <div>
      <h1>Opções de Viagem</h1>
      <Map route={route} />  {/* Passando a rota para o componente Map */}
      {/* Outras informações, como motoristas, serão listadas aqui */}
    </div>
  );
};

export default RideOptions;
