import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Map } from '../components/Map';
import axios from 'axios';

const RideOptions: React.FC = () => {
  const { state } = useLocation();
  const { routeData } = state || {};
  const [selectedDriver, setSelectedDriver] = useState(null);
  const navigate = useNavigate();

  const handleConfirm = async (driver: any) => {
    try {
      await axios.patch('http://localhost:3000/ride/confirm', {
        customer_id: routeData.customer_id,
        origin: routeData.origin,
        destination: routeData.destination,
        distance: routeData.distance,
        duration: routeData.duration,
        driver,
        value: driver.value,
      });
      navigate('/history');
    } catch (error) {
      console.error('Erro ao confirmar viagem:', error);
    }
  };

  return (
    <div>
      <h2>Opções de Viagem</h2>
      <Map route={routeData} />
      <ul>
        {routeData.options.map((driver: any) => (
          <li key={driver.id}>
            <p>{driver.name} - R${driver.value.toFixed(2)}</p>
            <button onClick={() => handleConfirm(driver)}>Escolher</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RideOptions;
