// src/pages/RequestRide.tsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Para redirecionamento
import Map from '../components/Map.tsx';  // Certifique-se de que o componente Map está funcionando corretamente
import axios from 'axios';

const RequestRide: React.FC = () => {
  const [userId, setUserId] = useState('');
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [error, setError] = useState('');
  const [tripOptions, setTripOptions] = useState<any>(null);

  // Hook de navegação para redirecionamento
  const navigate = useNavigate();

  // Função para enviar os dados para estimativa de viagem
  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/ride/estimate', {
        userId,
        origin,
        destination,
      });
      setTripOptions(response.data);
    } catch (err) {
      setError('Erro ao estimar a viagem');
    }
  };

  // Função para redirecionar para a página de histórico
  const handleGoToHistory = () => {
    navigate('/historico');
  };

  // Definindo uma rota de exemplo para o mapa (você pode modificar conforme sua necessidade)
  const route = {
    start: [51.505, -0.09],  // Coordenadas de origem
    end: [51.515, -0.1],     // Coordenadas de destino
    path: [
      [51.505, -0.09],
      [51.515, -0.1]
    ]
  };

  return (
    <div>
      <h1>Solicitação de Viagem</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="ID do Usuário"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <input
          type="text"
          placeholder="Endereço de Origem"
          value={origin}
          onChange={(e) => setOrigin(e.target.value)}
        />
        <input
          type="text"
          placeholder="Endereço de Destino"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <button type="submit">Estimar Viagem</button>
      </form>

      {error && <p>{error}</p>}

      {tripOptions && (
        <div>
          <h2>Opções de Viagem</h2>
          <ul>
            {tripOptions.drivers.map((driver: any) => (
              <li key={driver.id}>
                <p>{driver.name}</p>
                <p>{driver.description}</p>
                <p>{driver.vehicle}</p>
                <p>Avaliação: {driver.rating}</p>
                <p>Preço: {driver.price}</p>
                <button>Escolher</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Exibir o mapa com as opções */}
      <Map route={route} />

      {/* Botão para redirecionar para a tela de histórico */}
      <button onClick={handleGoToHistory}>Ir para Histórico</button>
    </div>
  );
};

export default RequestRide;
