import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const RequestRide: React.FC = () => {
  const [customerId, setCustomerId] = useState('');
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:3001/ride/estimate', {
        customer_id: customerId,
        origin,
        destination,
      });

      // Navega para a próxima tela de opções de viagem
      navigate('/ride-options', { state: { routeData: response.data } });
    } catch (err) {
      setError('Erro ao calcular a viagem. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Solicitação de Viagem</h1>
      <input 
        type="text" 
        placeholder="ID do Cliente" 
        value={customerId} 
        onChange={(e) => setCustomerId(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Endereço de Origem" 
        value={origin} 
        onChange={(e) => setOrigin(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Endereço de Destino" 
        value={destination} 
        onChange={(e) => setDestination(e.target.value)} 
      />
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Carregando...' : 'Estimar Viagem'}
      </button>
      {error && <p>{error}</p>}
    </div>
  );
};

export default RequestRide;
