import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Map from '../components/Map';
import axios from 'axios';

// Tipos para os dados que estamos recebendo
interface Driver {
  id: number;
  name: string;
  vehicle: string;
  rating: number;
  value: number;
}

interface RouteData {
  customer_id: string;
  origin: string;
  destination: string;
  distance: string;
  duration: string;
  start: [number, number];  // [latitude, longitude]
  end: [number, number];    // [latitude, longitude]
  path: [number, number][]; // Array de coordenadas [latitude, longitude]
  options: Driver[];        // Lista de motoristas disponíveis
}

const RideOptions: React.FC = () => {
  const { state } = useLocation();
  const routeData: RouteData | null = state?.routeData || null; // Tipagem do estado
  
  console.log("Dados de routeData:", routeData);
  const navigate = useNavigate();


  // Função para confirmar a viagem
  const handleConfirm = async (driver: Driver) => {
    if (!routeData) return;

    try {
      await axios.patch('http://localhost:3000/api/ride/confirm', {
        customer_id: routeData.customer_id,
        origin: routeData.origin,
        destination: routeData.destination,
        distance: routeData.distance,
        duration: routeData.duration,
        driver_id: { id: driver.id, name: driver.name },
        value: driver.value,
      });
      navigate('/history');
    } catch (error) {
      console.error('Erro ao confirmar viagem:', error);
      alert('Erro ao confirmar a viagem. Tente novamente.');
    }
  };

  if (!routeData) {
    return <p>Erro: Dados da rota não disponíveis.</p>;
  }

  return (
    <div>
      <h2>Opções de Viagem</h2>
      <Map route={{ start: routeData.start, end: routeData.end, path: routeData.path }} />
      <ul>
        {routeData.options.map((driver) => (
          <li key={driver.id}>
            <p>
              <strong>Motorista:</strong> {driver.name} <br />
              <strong>Veículo:</strong> {driver.vehicle} <br />
              <strong>Avaliação:</strong> {driver.rating}/5 <br />
              <strong>Preço:</strong> R${driver.value.toFixed(2)}
            </p>
            <button onClick={() => handleConfirm(driver)}>Escolher</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RideOptions;
