import React from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import { LatLngExpression } from 'leaflet';

interface MapProps {
  route: {
    start: LatLngExpression;  // Ponto A (origem)
    end: LatLngExpression;    // Ponto B (destino)
    path: LatLngExpression[]; // Caminho da rota (lista de pontos intermediários)
  };
}

const Map: React.FC<MapProps> = ({ route }) => {
  return (
    <MapContainer center={route.start} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {/* Marcador de Origem (Ponto A) */}
      <Marker position={route.start} />
      {/* Marcador de Destino (Ponto B) */}
      <Marker position={route.end} />
      {/* Desenhando a Rota */}
      <Polyline positions={route.path} color="blue" weight={5} />
    </MapContainer>
  );
};

export default Map;
