import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Map from '../components/Map';
import axios from 'axios';
import { RouteData, Driver } from '../types';  // Importe a interface corretamente

const RideOptions: React.FC = () => {
  const { state } = useLocation();
  const routeData: RouteData = state?.routeData || null; // Agora o TypeScript reconhece 'RouteData'
  const navigate = useNavigate();

  console.log("Dados de routeData:", routeData);

  const handleConfirm = async (driver: Driver) => {
    if (!routeData) return;
  
    try {
      const requestBody = {
        customer_id: routeData.customer_id, // Verifique se esse valor está vindo corretamente
        origin: routeData.origin,           // Verifique se origin está sendo enviado como string ou objeto
        destination: routeData.destination, // O mesmo para destination
        distance: routeData.distance,       // Certifique-se de que está no formato correto, pode ser string ou número
        duration: routeData.duration,       // O mesmo para duration
        driver: { id: driver.id, name: driver.name }, // O driver precisa ter a estrutura correta
        value: driver.value,                // Valor da viagem
      };
  
      console.log("Corpo da requisição:", requestBody); // Verifique o que está sendo enviado aqui
  
      await axios.patch('http://localhost:3000/api/ride/confirm', requestBody);
      navigate('/history');
    } catch (error) {
      console.error('Erro ao confirmar viagem:', error);
      alert('Erro ao confirmar a viagem. Tente novamente.');
    }
  };
  

  if (!routeData) {
    return <p>Erro: Dados da rota não disponíveis.</p>;
  }

  return (
    <div>
      <h2>Opções de Viagem</h2>
      {/* Passando o objeto completo de routeData para o Map */}
      <Map route={routeData} />
      <ul>
        {routeData.options.map((driver) => (
          <li key={driver.id}>
            <p>
              <strong>Motorista:</strong> {driver.name} <br />
              <strong>Veículo:</strong> {driver.vehicle} <br />
              <strong>Avaliação:</strong> {driver.rating}/5 <br />
              <strong>Preço:</strong> R${driver.value.toFixed(2)}
            </p>
            <button onClick={() => handleConfirm(driver)}>Escolher</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RideOptions;
