// src/pages/TravelHistoryPage.tsx
import React, { useState } from 'react';
import axios from 'axios';

const TravelHistoryPage = () => {
  const [userId, setUserId] = useState('');
  const [driverId, setDriverId] = useState('');
  const [history, setHistory] = useState<any>([]);
  const [error, setError] = useState('');

  const handleFilter = async () => {
    try {
      const response = await axios.get('/api/ride/history', {
        params: { userId, driverId },
      });
      setHistory(response.data);
    } catch (err) {
      setError('Erro ao carregar o histórico');
    }
  };

  return (
    <div>
      <h1>Histórico de Viagens</h1>
      <input
        type="text"
        placeholder="ID do Usuário"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <select
        value={driverId}
        onChange={(e) => setDriverId(e.target.value)}
      >
        <option value="">Todos os motoristas</option>
        {/* Aqui, liste os motoristas para filtro */}
      </select>
      <button onClick={handleFilter}>Aplicar Filtro</button>

      {error && <p>{error}</p>}
      {history.length > 0 && (
        <ul>
          {history.map((trip: any) => (
            <li key={trip.id}>
              <p>Data: {trip.date}</p>
              <p>Motorista: {trip.driver}</p>
              <p>Origem: {trip.origin}</p>
              <p>Destino: {trip.destination}</p>
              <p>Distância: {trip.distance}</p>
              <p>Tempo: {trip.time}</p>
              <p>Valor: {trip.value}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TravelHistoryPage;
